function url_clean(a){if(a.indexOf("?")!=-1){a=a.substr(0,a.indexOf("?"))}return a}function build_url(){var d=$('[name="check_src"]:checked');var b=$("#check_blacklist").is(":checked");var c=$("#check_whitelist").is(":checked");if(d.length==$('[name="check_src"]').length&&b&&c){var a=url_clean(document.location.href)+"?src=all"}else{var a=url_clean(document.location.href)+"?src=";d.each(function(){a+=$(this).val()+","});a=a.substring(0,a.length-1);if(b){a+="&black"}if(c){a+="&white"}}if(d.length==0&&!b&&!c){$("#url").html("-");$("#url").attr("href","javascript:void(0)")}else{$("#url").html(a);$("#url").attr("href",a)}}$(document).ready(function(){$('input[type="checkbox"]').change(function(){build_url()});$("#lang").change(function(){document.location.href=url_clean(document.location.href)+"?lang="+$("#lang").val()});$("[title").tooltip()});